// 잘못된 예시
function createServer() {
    const heavyObject = { data: new Array(10000) };
    setInterval(() => {
        console.log(heavyObject);
    }, 1000);
}

// 올바른 예시
function createServer() {
    const timer = setInterval(() => {
        const temporaryObject = { data: new Array(10000) };
        console.log(temporaryObject);
    }, 1000);

    // 서버 종료 시 타이머 정리
    process.on('SIGTERM', () => {
        clearInterval(timer);
    });
}
